import { GET_VIDEOGAMES, FILTER_BY_GENRE } from "../action-types";

let initialState = {
  videogames: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_VIDEOGAMES:
      return {
        ...state,
        videogames: action.payload,
      };

    case FILTER_BY_GENRE:
      const allVideogames = state.videogames;
      let statusFiltered = null;
      if (action.payload === "All") {
        statusFiltered = allVideogames;
      }

      allVideogames.map((el) => {
        for (let i = 0; i < el.genre.length; i++) {
          statusFiltered = el.genre.name === action.payload;
        }
      });

      return {
        ...state,
        videogames: statusFiltered,
      };
    default:
      return { ...state };
  }
};

export default rootReducer;
