import { GET_VIDEOGAMES, FILTER_BY_GENRE } from "../action-types";

let initialState = {
  videogames: [],
  allVideogames: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_VIDEOGAMES:
      return {
        ...state,
        videogames: action.payload,
        allVideogames: action.payload,
      };

    case FILTER_BY_GENRE:
      const filteredgames = state.allVideogames;

      let statusFiltered =
        action.payload === "All"
          ? filteredgames
          : filteredgames.filter((el) => el.genre.includes(action.payload));

      return {
        ...state,
        videogames: statusFiltered,
      };
    default:
      return { ...state };
  }
};

export default rootReducer;
