import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getVideogames } from "../../redux/actions";
import { Link } from "react-router-dom";
import NavBar from "../NavBar/NavBar";
import Pager from "../Pager/Pager";
import Footer from "../Footer/Footer";

const Home = () => {
  const dispatch = useDispatch();
  const allGames = useSelector((state) => {
    return state.videogames;
  });

  useEffect(() => {
    dispatch(getVideogames());
  }, []);

  return (
    <div>
      <NavBar />
      <div>
        <aside>
          <select>
            <option value="">Todos</option>
            <option value="">Filtrar por Genero (Y todas sus opciones)</option>
            <option value="">Origen api</option>
            <option value="">Origen db</option>
          </select>
          <select>
            <option value="ascAlf">Ascendente Alfabeticamente</option>
            <option value="descAlf">Descendente Alfabeticamente</option>
            <option value="ascRat">Ascendente Rating</option>
            <option value="descRat">Descendente Rating</option>
          </select>
        </aside>
        <article>
          <Pager />
        </article>
        <footer>
          <Footer />
        </footer>
      </div>
    </div>
  );
};

export default Home;
